import re

def normalizing(input_string):
    return re.sub(r'[^a-zA-Z0-9]', '', input_string.lower()) 

def normalizing1(input_string):
    return re.sub(r'[^a-zA-Z0-9\s]', '', input_string.lower()) 

def normalizing2(input_string):
    return re.sub(r'[^a-zA-Z0-9\S]', '', input_string.lower())

def normalizing3(input_string):
    return re.sub(r'[^\S]', '', input_string.lower()) 

def normalizing4(input_string):
    return re.sub(r'\W+', '', input_string.lower()) 

def normalizing5(input_string):
    return re.sub(r'\w', '', input_string.lower()) 
    
txt = "Watch_Dogs™: Special+ Edition"

print(normalizing(txt))
print(normalizing1(txt))
print(normalizing2(txt))
print(normalizing3(txt))
print(normalizing4(txt))
print(normalizing5(txt))
